import React, {Component} from 'react';
import LockContent from "../components/LockContent";
import {getUserData} from "../api/UserData";

class LockList extends Component {

    constructor(props) {
        super(props);
        this.state = {
            username: '',
            data: [[]]
          };
        this.state.size = "Размер замка";
        this.state.color = "Цвет замка ";
        this.state.name = "С кем повесили замочек ";
        this.state.message = "Сообщение:";
    }

    reloadUserData = () => {
        getUserData("11").then((data) => {
            this.setState({'data': data});
        });
    };

    render() {   
        this.reloadUserData();
        const smth = this.state.data[3];  
        console.log(smth[0]);
        let cardContent = (
            <div id="l-Text" class="l-Text">
                <p>{smth}</p>
          </div>
        );

        return (
            <div className="card" style={{width: '24rem' }}>
                <div className="form-group">
                  {cardContent}
                  <button type="submit" class="btn btn-danger" onClick={this.clickHandler}>Удалить</button>
                </div>
            </div>
    );
    }
}

export default LockList;
