import React, {Component} from 'react';
import LockContent from "../components/LockContent";
import {getUserData} from "../api/UserData";

class LockList extends Component {

    constructor(props) {
        super(props);
        this.state = {
            username: '',
            data: [[]]
          };
    }

    reloadUserData = () => {
        getUserData("11").then((data) => {
            this.setState({'data': data});
        });
    };

    clickHandler(id_lock){
        delete_lock_id(id_lock);
    }

    render() {   
        this.reloadUserData();
        const smth = this.state.data;  

        let cardContent = (
            <div id="l-Text" class="l-Text">
                <p>Пользователь {smth[0]}</p>
                <p>Дизайн {smth[1]}</p>
                <p>Человек {smth[2]}</p>
                <p>Сообщение {smth[3]}</p>
                <p>Размер {smth[4]}</p>
                <p>Индекс {smth[5]}</p>
          </div>
        );

        return (
            <div className="card" style={{width: '24rem' }}>
                <div className="form-group">
                  {cardContent}
                  <button type="submit" class="btn btn-danger" onClick={this.clickHandler(smth[5])}>Удалить</button>
                </div>
            </div>
    );
    }
}

export default LockList;
