    constructor(props) {
        super(props);
        this.state = {
            username: '',
            data: [[]]
          };
    }

    reloadUserData = () => {
        getUserData("11").then((data) => {
            this.setState({'data': data});
        });
    };

    clickHandler(id_lock){
        console.log("Мы туту");
        console.log(id_lock);
       // delete_lock_id(id_lock);
    }

    clickGetHandler(){
        this.reloadUserData();
        let cardContent = (
            <div id="l-Text" class="l-Text">
                <p>Пользователь {this.state.data[0]}</p>
