                <p>Человек {this.state.data[2]}</p>
                <p>Сообщение {this.state.data[3]}</p>
                <p>Размер {this.state.data[4]}</p>
                <p>Индекс {this.state.data[5]}</p>
          </div>
        );
    }

    render() {   



        return (
            <div className="card" style={{width: '24rem' }}>
                <div className="form-group">
                  {/* {cardContent}
