    return (
     //   <div className="col col-12 col-md-6 col-lg-4">
            <div className="card" style={{width: '9rem'}}>
                <div className="card-body">
                  {cardContent}
                  <a href="#" onClick={(e) => {}} className="btn btn-danger">Удалить</a>
    
                </div>
            </div>
