import React, {Component} from 'react';
import {sendInputValue} from "../api/InputValues";
import { Route, Link} from 'react-router-dom';
import '../style/auth.css';

class Auth extends Component {
    clickHandler() {
        const name = document.getElementById("name").value;
        const surname = document.getElementById("surname").value;
        const email = document.getElementById("email").value;
        const password = document.getElementById("password").value;
        sendInputValue(name, surname, email, password);
    }

    render() {
        return (
            <form class="form-horizontal">
                <div class="form-group">
                    <label for="exampleInputEmail1">Email</label>
                    <input type="email" class="form-control" id="exampleInputEmail1"></input>
                </div>
                <div class="form-group">
                    <label for="exampleInputPassword1">Пароль</label>
                    <input type="password" class="form-control" id="exampleInputPassword1"></input>
                </div>
                <div class="form-group">
                    <div class="main-checkbox">
                        <input value="None" id="checkbox1" name="check" type="checkbox"></input>
                        <label for="checkbox1"></label>
                    </div>
                    <span class="text">Запомнить</span>
                </div>
                <div class="form-group">
                    <button type="submit" class="btn btn-default">Войти</button>
                </div>
                <div class="form-group forgot-pass">
                    <button type="submit" class="btn btn-default">Забыли пароль</button>
                </div>
            </form>
        );
    }   
}
export default Auth;
