import React, {Component} from 'react';
import {sendInputValue} from "../api/InputValues";
import { Route, Link} from 'react-router-dom';
import '../style/auth.css';

class Auth extends Component {
    clickHandler() {
        const name = document.getElementById("name").value;
        const surname = document.getElementById("surname").value;
        const email = document.getElementById("email").value;
        const password = document.getElementById("password").value;
        sendInputValue(name, surname, email, password);
    }

    render() {
        return (
            <form>
            <p>Name</p>
            <div>
                <input type="radio" id="name" value="kate"></input>
                <label>kate</label>

                <input type="radio" id="name" value="ann"></input>
                <label>ann</label>

                <input type="radio" id="name" value="mari"></input>
                <label>mari</label>
            </div>

            <p>Surname</p>
            <div>
                <input type="radio" id="surname" value="smith"></input>
                <label>smith</label>

                <input type="radio" id="surname" value="popova"></input>
                <label>popova</label>

                <input type="radio" id="surname" value="smirnova"></input>
                <label>smirnova</label>
            </div>
            <p>
                 <label>Email</label>
                 <input type="text" id = "email"></input> 
            </p>
            <p>
                 <label>Пароль</label>
                 <input type="password" id="password"></input> 
            </p>  
            <div>
                <button onClick={this.clickHandler}>Submit</button>
            </div>
            </form>
