import AccountCircle from '.../node-modules/material-ui/icons/AccountCircle';
import AppBar from '.../node-modules/material-ui/core/AppBar';
import ChevronLeftIcon from '.../node-modules/material-ui/icons/ChevronLeft';
import ChevronRightIcon from '.../node-modules/material-ui/icons/ChevronRight';
import clsx from 'clsx';
import CssBaseline from '.../node-modules/material-ui/core/CssBaseline';
import Divider from '.../node-modules/material-ui/core/Divider';
import Drawer from '.../node-modules/material-ui/core/Drawer';
import IconButton from '.../node-modules/material-ui/core/IconButton';
import List from '.../node-modules/material-ui/core/List';
import ListItem from '.../node-modules/material-ui/core/ListItem';
import ListItemText from '.../node-modules/material-ui/core/ListItemText';
import MenuIcon from '.../node-modules/material-ui/icons/Menu';
import { NavLink } from 'react-router-dom'; import React from 'react';
import Toolbar from '.../node-modules/material-ui/core/Toolbar';
import Typography from '.../node-modules/material-ui/core/Typography';
import { makeStyles, useTheme } from '.../node-modules/material-ui/core/styles';

const drawerWidth = 240;
const useStyles = makeStyles(theme => ({
  root: {
    display: 'flex',
  },
  appBar: {
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: drawerWidth,
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  hide: {
    display: 'none',
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
  },
  drawerHeader: {
    display: 'flex',
    alignItems: 'center',
    padding: theme.spacing(0, 1),
    ...theme.mixins.toolbar,
    justifyContent: 'flex-end',
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: -drawerWidth,
  },
  contentShift: {
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: 0,
  },
}));

export default function PersistentDrawerLeft() {
  const classes = useStyles();
  const theme = useTheme();
  const [open, setOpen] = React.useState(false);

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  const arrayOfLink = [
    { url: '/', text: 'Home' },
    { url: '/tips', text: 'Tips' },
    { url: '/creationCV', text: 'Creation CV' },
    { url: '/list', text: ' Sign In' }
  ];

  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar
        position='fixed'
        className={clsx(classes.appBar, {
          [classes.appBarShift]: open,
        })}
      >
        <Toolbar>
          <IconButton
            color='inherit'
            aria-label='open drawer'
            onClick={handleDrawerOpen}
            edge='start'
            className={clsx(classes.menuButton, open && classes.hide)}
          >
            <MenuIcon />
          </IconButton>
          <Typography
            variant='h6'
            noWrap
          >
            CV
          </Typography>
          <div className='login-icon'>
            <NavLink
              to='/list'
              className='list-of-item'
            >
              <IconButton
                aria-label='account of current user'
                aria-controls='primary-search-account-menu'
                aria-haspopup='true'
                color='inherit'
              >
                <AccountCircle />
              </IconButton>
            </NavLink>
          </div>
        </Toolbar>
      </AppBar>
      <Drawer
        className={classes.drawer}
        variant='persistent'
        anchor='left'
        open={open}
        classes={{
          paper: classes.drawerPaper,
        }}
      >
        <div className={classes.drawerHeader}>
          <IconButton onClick={handleDrawerClose}>
            {theme.direction === 'ltr' ? <ChevronLeftIcon /> : <ChevronRightIcon />}
          </IconButton>
        </div>
        <Divider />
        <List>
          {arrayOfLink.map((item, index) => (
            <NavLink
              to={item.url}
              className='list-of-item'
              key={index}
            >
              <ListItem
                button
                key={index}
              >
                <ListItemText primary={item.text} />
              </ListItem>
            </NavLink>
          ))}
        </List>
        <Divider />
      </Drawer>
      <main
        className={clsx(classes.content, {
          [classes.contentShift]: open,
        })}
      >
        <div className={classes.drawerHeader} />
      </main>
    </div>
  );
}
