import React, {Component} from 'react';
import '../style/auth.css';
import {sendLockData} from "../api/SendLockData";

class Lock extends Component {

    constructor(props) {
        super(props);
        this.state = {
            username: this.props.username
          };
    }
    clickHandler() {
        const username = "11";
        const person = document.getElementById("person").value;
        const design = document.getElementById("design").value;
        const size = document.getElementById("size").value;
        const message = document.getElementById("message").value;
        sendLockData(username, person, design, size, message);
        console.log(person, design, size, message);
    }
    render() {
        return (
            <div className="answer_container">
                <div class="l-Title">
                    <p>Выбери дизайн замочка</p>
                </div>
                <div>
            <select class="image-picker show-html">
                    <option data-img-src='../style/img/замок.png' data-img-class="first" data-img-alt="Page 1" value="1">  Page 1  </option>
                    <option data-img-src={require('../style/img/замок2.png')} data-img-class="first" data-img-alt="Page 1" value="1">  Page 1  </option>
                    <option data-img-src={require('../style/img/замок3.png')} data-img-class="first" data-img-alt="Page 1" value="1">  Page 1  </option>
                </select>
                </div>
                <div class="l-Title">
                    <p>Выбери размер замочка</p>
                </div>
                <div>
                    <table>
                    <tr>
                        <td>
                           <p> Маленький </p>
                        </td>
                        <td>
                            <p>Средний</p>                     
                        </td>
                        <td>
                            <p>Большой</p>               
                        </td>
                    </tr>
                    <tr>
                        <td>
                        </td>
                        <td>
                        <select id = "size" class="form-control">
                            <option value="little">Маленький</option>
                            <option value="middle">Средний</option>
                            <option selected value="big">Большой </option>
                        </select>
                        </td>
                        <td> 
                        </td>
                    </tr>
                    </table>
                </div>
                <div class="l-Title">
                    <p>Введи имя человека, вместе с которым вы хотите повесить замочек</p>
                </div>
                <p>
                <input type="text" class="form-control" id="person"></input>
                </p>
                <div class="l-Title">
                    <p>Оставь сообщение</p>
                </div>
                <p>
                <input type="text" class="form-control" id="message"></input>
                </p>
                <button class="button2" onClick={this.clickHandler}>Повесить на мост</button>
            </div>
        );
    }    

}
export default Lock;
